import './App.scss';

import {Switch,Route, BrowserRouter as Router} from 'react-router-dom';

import React, { Component, Fragment } from 'react';


import { AuthProvider } from './contexts/AuthContext';

import NavBar from './Components/NavBar';

import PrivateRoute from './Components/PrivateRoute';

import HomePage from './Pages/HomePage';
import LoginPage from './Pages/LoginPage';
import DriverSignupPage from './Pages/DriverSignupPage';
import RiderSignupPage from './Pages/RiderSignupPage';
import Dashboard from './Pages/Dashboard';
import AuthSideBar from './Components/AuthSideBar';
import app from './firebase';
var user = app.auth().currentUser
console.log(user)
class App extends Component{
    constructor(props) {
      super(props)
      this.state={
        currentuser:user
      }

      }



   render(){
    //  let body;
    // console.log(window.location.pathname)
    console.log(this.state.currentuser)
let HideHeader;
    if(this.state.currentuser)
    {
      HideHeader=<AuthSideBar/>
    }
    else
    {
      HideHeader=<NavBar/>
    }
     return ( 
          <div className="App">
            <Router>
                <div className="sidebar nav-toggle">
                    {HideHeader}
                </div>
                <div className="nav-btn" onClick={()=>this.setState({show: !this.state.show})}>
                  <div className="lines-1"></div>
                  <div className="lines-2"></div>
                  <div className="lines-3"></div>
                </div>
            <div className="main-content">
                <div className="content">
                        <AuthProvider>
                              <Switch>

                              <PrivateRoute path="/dashboard" exact>
                                  <Dashboard />
                                </PrivateRoute>

                                <Route path="/" exact>
                                  <HomePage />
                                </Route>
                                <Route path="/login" exact>
                                  <LoginPage />
                                </Route>
                                <Route path="/driversignup" exact>
                                  <DriverSignupPage />
                                </Route>
                                <Route path="/ridersignup" exact>
                                  <RiderSignupPage />
                                </Route>
                              </Switch>
                        </AuthProvider>
                </div>

              </div>
               </Router> 
               </div>
        );
      }


   }

export default App;
