import React, { Component } from 'react';
import Web3 from 'web3';
import Identicon from 'identicon.js';
import '../App.css';
import user from '../abis/user.json'
import AuthNavBar from '../Components/AuthNavBar'
import Main from '../Components/Main'
import DriverID from '../Components/DriverID'

import User from '../Components/User'

//Declare IPFS
const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' }) // leaving out the arguments will default to these values


class Dashboard extends Component {
  constructor(props) {
    super(props)
    this.state = {
      balance:'',
      account: '',
      user1: null,
      users:[],
      driver:[],
      ride:[],
      loading: true,

    }
  }
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3.providers.HttpProvider("127.0.0.1:9545")
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()


    this.setState({ account: accounts[0] })
    const unitbal = await web3.eth.getBalance(accounts[0])
    const  balanceacc = await web3.utils.fromWei(unitbal,'ether')

    this.setState({balance: balanceacc})

    const networkId = await web3.eth.net.getId()
    const networkData = user.networks[networkId]
    console.log(networkData);

    if (networkData) {
      const user1 = new web3.eth.Contract(user.abi, networkData.address)
      console.log(user1);

      this.setState({ user1 })

      const reqCounts = await user1.methods.reqCount().call()
      this.setState({ reqCounts })

      const imgCounts = await user1.methods.imgCount().call()
      this.setState({ imgCounts })

      const rideCounts = await user1.methods.rideCount().call()
      this.setState({ rideCounts })

      //Load requests
      for (var i = 1; i <= reqCounts; i++) {
        const users_var = await user1.methods.users(i).call()

        this.setState({
          users: [...this.state.users,users_var]
        })
      }

      //Load images

      for (var j=1; j<=imgCounts; j++){
        const img_var = await user1.methods.driver(j).call()

        this.setState({
          driver: [...this.state.driver,img_var]
        })
      }
     /** */

     for (var k=1; k<=rideCounts; k++){
      const ride_var = await user1.methods.ride(k).call()
      console.log(ride_var);

      this.setState({
        ride: [...this.state.ride,ride_var]
      })
    }

    } else {
      window.alert('User contract not deployed to detected network.')
    }

  }


  captureFile = event => {

    event.preventDefault()
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)

    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) })
      console.log('buffer', this.state.buffer)
    }
  }



  rideReq = (source,destination,date,time) => {
    this.setState({loading:true })
      this.state.user1.methods.rideReq(source,destination,date,time).send({from: this.state.account}).on('transactionHash', (hash) => {
        this.setState({ loading: false })
      })
  }

  uploadRide = (source,destination,date,time,rider,amount) => {
    this.setState({loading:true })
      this.state.user1.methods.uploadRide(source,destination,date,time,rider,amount).send({from: this.state.account}).on('transactionHash', (hash) => {
        this.setState({ loading: false })
      })
  }



  uploadImaging = (drivername,hash,verified) =>
  {
    this.state.user1.methods.uploadImage(hash,drivername,verified).send({from: this.state.account}).on('transactionHash',(hash) => {
      this.setState({loading:false})
    })

  }

  uploadImage = drivername => {
    console.log("submitting file to ipfs...")

    ipfs.add(this.state.buffer, (error, result) => {
      console.log('Ipfs result', result)
      if (error) {
        console.error(error)
        return
      }

    this.setState({loading:true})
      this.state.user1.methods.uploadImg(result[0].hash,drivername,false).send({from: this.state.account}).on('transactionHash',(hash) => {
        this.setState({loading:false})
      })
    })
  }

  verifyDriver = (id,hashs,name,author) => {

  this.state.user1.methods.verifyDriver(id,hashs,name,author).send({from: this.state.account}).on('transactionHash',(hash) => {
    this.setState({loading:false})
  })
  }

  tipImageOwner = (id, tipAmount) =>{
    this.setState({ loading: true })
    this.setState({ loading: false })
    this.state.cryptridez.methods.tipImageOwner(id).send({ from: this.state.account, value: tipAmount }).on('transactionHash', (hash) => {
    })
  }

  rideAmount = (id,amount) =>{
    this.state.user1.methods.ridePayment(id).send({ from: this.state.account, value: amount }).on('transactionHash', (hash) => {
    })
  }





  render() {
    let body
    const authorizedAccounts = ['0x50Fe6C433c41595396044A9bcdF9500963D4e236']
    const authorizedAccounts2 = ['0x2fb5eBC35A6d6e7c26bEC01122b34d84D7727aD9']
    const authorized = authorizedAccounts.includes(this.state.account)
        if(authorizedAccounts.includes(this.state.account)){

          body = 
          <Main
          account={this.state.account}
          users={this.state.users}
          driver={this.state.driver}
          captureFile={this.captureFile}
          uploadImage={this.uploadImage}
          uploadRide = {this.uploadRide}
        />
        }
        else if(authorizedAccounts2.includes(this.state.account)){
          body = 
          <DriverID
          account={this.state.account}
          users={this.state.users}
          driver={this.state.driver}
          captureFile={this.captureFile}
          uploadImage={this.uploadImage}
          uploadImaging = {this.uploadImaging}
          verifyDriver = {this.verifyDriver}
        />

        }
        else{
          body = 
          <User
          account = {this.state.account}
          rideReq = {this.rideReq} 
          users={this.state.users}
          uploadRide = {this.uploadRide}
          ride = {this.state.ride}
          rideAmount = {this.rideAmount}

          />
                  }

    return (
      <div>
        <AuthNavBar account={this.state.account}
        balance= {this.state.balance} />
        { body }

      </div>
    );
  }
}

export default Dashboard;
