import React, { Component,useState } from 'react'
import avatar from '../img/car.png';
import {NavLink,useHistory} from 'react-router-dom';
import {useAuth}  from "../contexts/AuthContext"
import {auth} from "../firebase"
import Identicon from 'identicon.js';
import walletphoto from "../img/wallet.png"
import "../styles/navbar.css"

const NavBar=(props)=>{

    const [error, setError] = useState("")
    const { logout } = useAuth()
    const history = useHistory()

    async function handleLogout() {
        setError("")

        try {
        await logout()
        history.push("/")
        } catch {
        setError("Failed to log out")
        }
    }

    // auth.onAuthStateChanged(function(user) {
        var user=auth.currentUser;
        if (user) {
        //   console.log(user)
        //   console.log(props.balance,props.account)
          return (
            <div className="NavBar">
                       <nav className="nav">
                           <div className="profile">
                               <img src={avatar} alt=""/>
                            </div>
                            <ul className="nav-items">

                            <div
                                className="nav-item"
                                href="#"
                                target="_blank"
                                rel="noopener noreferrer"
                                >
                                {/* <img src={photo} width="30" height="30" className="d-inline-block align-top" alt="" /> */}
                                <h2 className="crypttitle">Crypt Ridez &emsp;&emsp;&emsp;</h2>
                                {/* <hr /> */}
                                <hr  color='black' ></hr>
                                <small className="text-secondary">
                                            <small id="account">&nbsp;</small>
                                            </small>
                                            <small className="text-secondary">
                                            </small>
                                            { props.account
                                            ? <img
                                                className='ml-2'
                                                width='30'
                                                height='30'
                                                src={`data:image/png;base64,${new Identicon(props.account, 30).toString()}`}
                                            />  
                                            : <span></span>
                                            }
                                            <small id="account">{props.account}</small>
                                <hr  color='black' ></hr>
                                <img src={walletphoto} width="30" height="30" className="d-inline-block align-top" alt="" />
                                <p>&nbsp;{props.balance}&nbsp;Ethers</p>
                            </div>

                           <li className="nav-item">
                                        <small className="text-secondary">
                                            <small id="account">&nbsp;</small>
                                            </small>
                                            <small className="text-secondary">
                                            </small>
                                            { props.account
                                            ? <img
                                                className='ml-2'
                                                width='30'
                                                height='30'
                                                src={`data:image/png;base64,${new Identicon(props.account, 30).toString()}`}
                                            />  
                                            : <span></span>
                                            }
                                            <small id="account">{props.account}</small>

                            </li>
                               <li className="nav-item">

                               <NavLink className="nav-link" to="/" exact>
                                   Book Ride
                               </NavLink>

                               </li>

                               <li></li>

                                        <li>  <button type="submit" className="btn btn-secondary btn-block" onClick={handleLogout}>Logout</button></li>
                                        {/* </ul> */}
                           </ul>

                           <footer className="footer">
                               <p>
                                   @RIDE SHARING
                               </p>
                       </footer>
                       </nav>

                   </div>
          )
        } else {
            console.log(user)
            console.log("NOT LOGGED")
            return (
                <div className="NavBar">
                           <nav className="nav">
                               <div className="profile">
                                   <img src={avatar} alt=""/>
                                </div>

                               <ul className="nav-items">
                                   <li className="nav-item">
                                   <NavLink className="nav-link" to="/" exact>
                                       Home
                                   </NavLink>

                                   </li>

                                   <li className="nav-item">
                                   <NavLink className="nav-link" exact to="/login">
                                       Login
                                       </NavLink>


                                       </li>
                                       <li className="nav-item">
                                       <NavLink className="nav-link" to="/ridersignup" exact>
                                           Rider Sign Up
                                           </NavLink>

                                       </li>
                                       <li className="nav-item">
                                       <NavLink className="nav-link" to="/driversignup" exact>
                                           Driver Sign Up
                                           </NavLink>

                                       </li>

                                   {/* <li className="nav-item">
                                       <NavLink to="/bookride" exact activeClassName="active">
                                           Book Ride
                                       </NavLink>
                                   </li> */}
                                   {/* <li className="nav-item">
                                       <NavLink to="/history" exact activeClassName="active">
                                           Ride History
                                       </NavLink>
                                   </li> */}
                               </ul>

                               <footer className="footer">
                                   <p>
                                       @RIDE SHARING
                                   </p>
                           </footer>
                           </nav>

                       </div>
            )
        }
    //   });
        // console.log(currentUser.currentUser)
