import './App.scss';

import {Switch,Route, BrowserRouter as Router} from 'react-router-dom';

import React, { Component } from 'react';
import Web3 from 'web3';

import user from './abis/user.json';

import { AuthProvider } from './contexts/AuthContext';

import NavBar from './Components/NavBar';
import PrivateRoute from './Components/PrivateRoute';

import HomePage from './Pages/HomePage';
import LoginPage from './Pages/LoginPage';
import SignUpPage from './Pages/SignUpPage';
import Dashboard from './Pages/Dashboard';



// IPFS for ID proofs of Driver
const ipfsClient = require('ipfs-http-client');
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' }); // leaving out the arguments will default to these values

const authorizedAccounts = ['0x50Fe6C433c41595396044A9bcdF9500963D4e236']
const authorizedAccounts2 = ['0x2fb5eBC35A6d6e7c26bEC01122b34d84D7727aD9']

class App extends Component{
    constructor(props) {
      super(props)
      this.state = {
        navToggle:false,

      }
    }

   render(){
    //  let body;

     return ( 
          <div className="App">
            <Router>
                <div className={`sidebar ${this.state.navToggle ? 'nav-toggle': ''}`}>
                    <NavBar />
                </div>
                <div className="nav-btn" onClick={this.toggle}>
                  <div className="lines-1"></div>
                  <div className="lines-2"></div>
                  <div className="lines-3"></div>
                </div>
            <div className="main-content">
                <div className="content">
                        <AuthProvider>
                              <Switch>
                              <PrivateRoute path="/dashboard" exact>
                                  <Dashboard />
                                </PrivateRoute>
                                <Route path="/" exact>
                                  <HomePage />
                                </Route>
                                <Route path="/login" exact>
                                  <LoginPage />
                                </Route>
                                <Route path="/signup" exact>
                                  <SignUpPage />
                                </Route>
                              </Switch>
                        </AuthProvider>
                </div>

              </div>
               </Router> 
               </div>
        );
      }


   }

export default App;
