import React, { Component } from 'react';
import Identicon from 'identicon.js';
import walletphoto from '../img/wallet.png'

class AuthNavBar extends Component {
  async handleLogout() {
    setError("")

    try {
      await logout()
      history.push("/")
    } catch {
      setError("Failed to log out")
    }
  }

  render() {
    return (
      <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
        <a
          className="navbar-brand col-sm-3 col-md-2 mr-0"
          href="#"
          target="_blank"
          rel="noopener noreferrer"
        >
          {/* <img src={photo} width="30" height="30" className="d-inline-block align-top" alt="" /> */}
          Crypt Ridez &emsp;&emsp;&emsp;
          <img src={walletphoto} width="30" height="30" className="d-inline-block align-top" alt="" />
          &nbsp;{this.props.balance}&nbsp;Ethers
        </a>
        <ul className="navbar-nav px-3">
          <li></li>
          <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
          <small className="text-secondary">
              <small id="account">&nbsp;</small>
            </small>
            <small className="text-secondary">
              <small id="account">{this.props.account}</small>
            </small>
            { this.props.account
              ? <img
                className='ml-2'
                width='30'
                height='30'
                src={`data:image/png;base64,${new Identicon(this.props.account, 30).toString()}`}
              />  
              : <span></span>
            }
          </li>
          <li>  <button type="submit" className="btn btn-secondary btn-block" onClick={handleLogout}>Logout</button></li>
        </ul>
      </nav>
    );
  }
}

export default AuthNavBar;
