import { useState, useEffect } from "react";
import './Signup.css';
import { db } from "../firebase-config";
import Tittle from '../Components/Tittle'
import {collection,getDocs,addDoc} from "firebase/firestore";
import React from 'react';
function SignupPage () {
  {
    const [newName, setnewName] = useState("");
    const [newUsername, setnewUsername] = useState("");
    const [newcarno, setnewcarno] = useState("");
    const [newCity, setnewCity] = useState("");
    const [newnumseats, setnewnumseats] = useState(0);
    const [newcartype, setnewcartype] = useState("");
    const [newPassword, setnewPassword] = useState("");
    const [newCpassword, setnewCpassword] = useState("");

    const [users, setUsers] = useState([]);
    const usersCollectionRef = collection(db, "signup");

    const createUser = async () => {
      await addDoc(usersCollectionRef, { Name: newName, Username:newUsername, carno:newcarno, City:newCity, numseats: Number(newnumseats), cartype: newcartype,Password:newPassword, Cpassword:newCpassword });
    };

    useEffect(() => {
      const getUsers = async () => {
        const data = await getDocs(usersCollectionRef);
        setUsers(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
      };

      getUsers();
    }, []);

  {
    return(
      <div className="SignupPage">
          <Tittle title={'Sign Up'} span={'Sign Up'} />

       <div className="signup-container">
        <div className="signup-form-box">
          <div className="header-form">
            <h4 className="text-primary text-center"><i className="fa fa-user-circle" style={{fontSize:"110px"}}></i></h4>
            <div className="image">
            </div>
          </div>
          <div className="body-form">
           <form>
  <div className="input-group mb-3">
  <input type="text" className="form-control" placeholder="Name" onChange={(event) => { setnewName(event.target.value);}} id="Name" />
</div>
<div className="input-group mb-3">
<input type="text" className="form-control" placeholder="Username" onChange={(event) => { setnewUsername(event.target.value);}} id="Username" />
</div>
<div className="input-group mb-3">
  <input type="text" className="form-control" placeholder="Car Number" onChange={(event) => { setnewcarno(event.target.value);}} id="carno"  />
</div>
<div className="input-group mb-3">
  <input type="text" className="form-control" placeholder="City" onChange={(event) => { setnewCity(event.target.value);}} id="City" />
</div>
<div className="input-group mb-3">
  <input type="number" className="form-control" placeholder="Number of seats" onChange={(event) => { setnewnumseats(event.target.value);}} id="numseats" />
</div>
<div className="input-group mb-3">
  <input type="text" className="form-control" placeholder="Car Type" onChange={(event) => { setnewcartype(event.target.value);}} id="cartype" />
</div>
<div className="input-group mb-3">
  <input type="password" className="form-control" placeholder="Password" onChange={(event) => { setnewPassword(event.target.value);}} id="Password" />
</div>
<div className="input-group mb-3">
  <input type="password" className="form-control" placeholder="Confirm Password" onChange={(event) => { setnewCpassword(event.target.value);}} id="Cpassword"  />
</div>
 <button type="button" className="btn btn-secondary btn-block"  onClick={createUser}>SIGN UP</button>
 <div className="message">
<div><input type="checkbox" /> Remember ME</div>
 <div><a href="/login">Already registered?</a></div>
 </div>
   </form>
            <div className="social">
              <a href="#"><i className="fab fa-facebook"></i></a>
              <a href="#"><i className="fab fa-twitter-square"></i></a>
              <a href="#"><i className="fab fa-google"></i></a>
            </div>
          </div>
        </div>
       </div>  
       </div> 
    )
  }
}
}
export default SignupPage
