import React, { Component,useState } from 'react';
import Identicon from 'identicon.js';
import {useAuth}  from "../contexts/AuthContext"
import { useHistory } from "react-router-dom"
import walletphoto from "../img/wallet.png"
import "./AuthNavBar.css"

const AuthNavBar=(props)=>{
  const [error, setError] = useState("")
    const { logout } = useAuth()
    const history = useHistory()

    async function handleLogout() {
        setError("")

        try {
        await logout()
        history.push("/")
        } catch {
        setError("Failed to log out")
        }
    }
        return (
          // className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow "
          <nav >
        <a
          className="navbar-brand col-sm-3 col-md-2 mr-0"
          href="#"
          target="_blank"
          rel="noopener noreferrer"
        >
          {/* <img src={photo} width="30" height="30" className="d-inline-block align-top" alt="" /> */}
          Crypt Ridez &emsp;&emsp;&emsp;
          <img src={walletphoto} width="30" height="30" className="d-inline-block align-top" alt="" />
          &nbsp;{props.balance}&nbsp;Ethers
        </a>
        <ul className="navbar-nav px-3">
          <li></li>
          <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
          <small className="text-secondary">
              <small id="account">&nbsp;</small>
            </small>
            <small className="text-secondary">
              <small id="account">{props.account}</small>
            </small>
            { props.account
              ? <img
                className='ml-2'
                width='30'
                height='30'
                src={`data:image/png;base64,${new Identicon(props.account, 30).toString()}`}
              />  
              : <span></span>
            }
          </li>
          <li>  <button type="submit" className="btn btn-secondary btn-block" onClick={handleLogout}>Logout</button></li>
        </ul>
      </nav>
    );
    }

